const rest = {
    get: function(mUrl, mData){
        return $.ajax({
            "type": "GET",
            "url": mUrl,
            "data": mData,
            "contentType": "application/json; charset=utf-8",
        });
    }
}

const address = {
    info: async function(lng, lat) {
        const api = `/address/info`
        const res = await $.when(rest.get(api, {lng: lng, lat: lat}));
        return res
    }
}
